import React from 'react'
import {
    HStack,
    VStack,
    Text,
    IconButton,
    StackDivider,
    Spacer,
    Badge,
    Heading,
    Modal,
    ModalOverlay,
    ModalContent,
    ModalHeader,
    ModalCloseButton,
    ModalBody,
    Input,
    ModalFooter,
    Button,
} from '@chakra-ui/react';
import {FaTrash, FaPen, FaCheck} from "react-icons/fa";

type Props = TodoProps & {
    updateTodo: (todo: Todo) => void
    deleteTodo: (_id: string) => void
}

function handleEditInputChange(e, id) {
    setModalValue({...modalValue, text: e.target.value});
}

function handleEditSubmit(e) {
    e.preventDefault();
    editTodo(modalValue.id, modalValue)
    setModalValue("")
    setIsOpen(false)
}

const Todo: React.FC<Props> = ({todos, updateTodo, deleteTodo}) => {
    return (
        <VStack
            divider={<StackDivider/>}
            borderColor='gray.100'
            borderWidth='2px'
            p='4'
            borderRadius='lg'
            w='100%'
            maxW={{base: '90vw', sm: '80vw', lg: '50vw', xl: '40vw'}}
            alignItems='stretch'
        >
            {todos.map((todo) => (
                <HStack key={todo._id}>
                    <Heading fontSize='xl'>{todo.name}</Heading>
                    <Text mt={4}>{todo.description}</Text>
                    <Spacer/>
                    <Badge m='15' fontSize='0.8em' colorScheme={todo.status === 'incomplete' ? 'red' : 'green'}>
                        {todo.status}
                    </Badge>
                    <Spacer/>
                    <IconButton
                        color="green.500"
                        icon={<FaCheck/>}
                        isRound={true}
                        onClick={() => updateTodo(todo)}
                        aria-label={"lege"}/>
                    <IconButton
                        color="blue.500"
                        icon={<FaPen/>}
                        isRound={true}
                        onClick={() => updateTodo(todo)}
                        aria-label={"lege"}/>
                    <IconButton
                        color="red.500"
                        icon={<FaTrash/>}
                        isRound={true}
                        onClick={() => deleteTodo(todo._id)}
                        aria-label={"lege"}/>

                    {/* modal for editing a todo */}
                    <Modal isOpen={isOpen} onClose={onClose}>
                        <ModalOverlay/>
                        <ModalContent>
                            <ModalHeader>Edit Your Todo</ModalHeader>
                            <ModalCloseButton/>
                            <form onSubmit={handleEditSubmit}>
                                <ModalBody>
                                    <Input
                                        value={modalValue.text}
                                        key={modalValue.id}
                                        variant="outline"
                                        type="text"
                                        placeholder="Update your todo..."
                                        onChange={handleEditInputChange}/>
                                </ModalBody>
                                <ModalFooter>
                                    <Button colorScheme="teal" mr={3} onClick={onClose}>
                                        Close
                                    </Button>
                                    <Button type="submit" colorScheme="teal" mr={3}>
                                        Update
                                    </Button>
                                </ModalFooter>
                            </form>

                        </ModalContent>
                    </Modal>
                </HStack>
            ))}
        </VStack>
    )
}

export default Todo
