import React, {forwardRef, useState} from 'react'
import {
    Box,
    Button,
    HStack,
    Input,
    useToast,
    Text,
    VStack,
    StackDivider,
    Flex,
    Heading,
    FormControl, FormLabel, InputGroup, InputRightElement, CircularProgress
} from '@chakra-ui/react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

type Props = {
    createTodo: (e: React.FormEvent, formData: Todo | any) => void,
}

const AddTodo: React.FC<Props> = ({createTodo}) => {
    const [content, setContent] = useState({
        name: '',
        description: '',
        startDate: new Date(),
        endDate: new Date()
    });


    const toast = useToast();

    const onChange = e => {
        setContent({...content, [e.target.name]: e.target.value});
    };

    function handleSubmit(e) {
        e.preventDefault();
        if (!content) {
            toast({
                title: 'No content',
                status: 'error',
                duration: 2000,
                isClosable: true,
            });
            return;
        } else {
            createTodo(e, content);
        }
    }

    return (
        <><Flex width="full" align="center" justifyContent="center">
            <Box
                p={8}
                maxWidth="500px"
                borderWidth={1}
                borderRadius={8}
                boxShadow="lg"
            >
                <Box textAlign="center">
                    <Heading>Login</Heading>
                </Box>
                <Box my={4} textAlign="left">
                    <form onSubmit={handleSubmit}>
                        <HStack mt='8'>
                            <FormControl isRequired>
                                <FormLabel>Todo Name</FormLabel>
                                <Input
                                    size='lg'
                                    placeholder='name'
                                    name='name'
                                    onChange={onChange}/>
                                <FormLabel>Todo Name</FormLabel>
                                <Input
                                    size='lg'
                                    placeholder='name'
                                    name='name'
                                    onChange={onChange}/>
                            </FormControl>
                        </HStack>
                        <FormControl isRequired mt={6}>
                            <FormLabel>Password</FormLabel>
                            <InputGroup>
                                <Input
                                    size='lg'
                                    placeholder='description'
                                    name='description'
                                    onChange={onChange}/>
                                <InputRightElement width="3rem">
                                    <Button
                                        h="1.5rem"
                                        size="sm"
                                        onClick={handleSubmit}
                                    >
                                    </Button>
                                </InputRightElement>
                            </InputGroup>
                        </FormControl>
                        <Button
                            variant="outline"
                            type="submit"
                            width="full"
                            mt={4}
                        >
                            Submit
                            {/*<CircularProgress*/}
                            {/*    isIndeterminate*/}
                            {/*    size="24px"*/}
                            {/*    color="teal"/>*/}
                        </Button>
                    </form>
                </Box>
            </Box>
        </Flex>
            <form>
                <HStack mt='8'>
                    <Input
                        placeholder='name'
                        name='name'
                        onChange={onChange}/>
                    <Input
                        placeholder='description'
                        name='description'
                        onChange={onChange}/>
                </HStack>
                <VStack
                    divider={<StackDivider borderColor='gray.200'/>}
                    spacing={4}
                    align='stretch'
                >
                    <Box>
                        <Text fontSize='lg'> Start Date: </Text>
                        <DatePicker selected={content.startDate}
                                    onChange={(date: Date) => setContent({
                                        ...content,
                                        startDate: date
                                    })}/>
                    </Box>
                </VStack>
                <HStack mt='8' align='center'>
                    <Button onClick={handleSubmit} colorScheme='pink' px='8' type='submit'>
                        Add Todo
                    </Button>
                </HStack>
            </form>
        </>
    )
}

export default AddTodo
