import React, {forwardRef, useState} from 'react'
import {
    Box,
    Button,
    HStack,
    Input,
    useToast,
    Text,
    VStack,
    StackDivider,
    Flex,
    Heading,
    FormControl, FormLabel, InputGroup, InputRightElement, CircularProgress, Container
} from '@chakra-ui/react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

type Props = {
    createTodo: (e: React.FormEvent, formData: Todo | any) => void,
}

const AddTodo: React.FC<Props> = ({createTodo}) => {
    const [content, setContent] = useState({
        name: '',
        description: '',
        startDate: new Date(),
        endDate: new Date()
    });


    const toast = useToast();

    const onChange = e => {
        setContent({...content, [e.target.name]: e.target.value});
    };

    function handleSubmit(e) {
        e.preventDefault();
        if (!content) {
            toast({
                title: 'No content',
                status: 'error',
                duration: 2000,
                isClosable: true,
            });
            return;
        } else {
            createTodo(e, content);
        }
    }

    return (
        <Flex width="full" align="center" justifyContent="center" my={15}>
            <Box
                p={8}
                maxWidth="600px"
                borderWidth={1}
                borderRadius={8}
                boxShadow="lg"
                width='50%'
            >
                <Box my={2}>
                    <form onSubmit={handleSubmit}>
                        <FormControl isRequired width='40%'>
                            <FormLabel>Todo Name</FormLabel>
                            <Input
                                size='lg'
                                placeholder='name'
                                name='name'
                                onChange={onChange}/>
                        </FormControl>

                        <FormControl isRequired width='40%'>
                            <FormLabel>Todo Description</FormLabel>
                            <Input
                                size='lg'
                                placeholder='description'
                                name='description'
                                onChange={onChange}/>
                        </FormControl>

                        <FormControl width='40%'>
                            <FormLabel>Start Date</FormLabel>
                            <DatePicker selected={content.startDate}
                                        onChange={(date: Date) => setContent({
                                            ...content,
                                            startDate: date
                                        })}/>
                        </FormControl>
                        <FormControl width='40%'>
                            <FormLabel>End Date</FormLabel>
                            <DatePicker selected={content.endDate}
                                        onChange={(date: Date) => setContent({
                                            ...content,
                                            endDate: date
                                        })}/>
                        </FormControl>
                        <Box align='center'>
                            <Button
                                alignContent='center'
                                type="submit"
                                width="50%"
                                mt={4}
                                colorScheme='pink'
                                onClick={handleSubmit}
                            >
                                Submit
                            </Button>
                        </Box>
                    </form>
                </Box>
            </Box>
        </Flex>
    )
}

export default AddTodo
