import React, {useState} from 'react'
import {
    HStack,
    VStack,
    Text,
    IconButton,
    StackDivider,
    Spacer,
    Badge,
    Heading,
    Modal,
    ModalOverlay,
    ModalContent,
    ModalHeader,
    ModalCloseButton,
    ModalBody,
    Input,
    ModalFooter,
    Button,
} from '@chakra-ui/react';
import {FaTrash, FaPen, FaCheck} from "react-icons/fa";

type Props = TodoProps & {
    todos: Todo[],
    updateTodo: (todo: Todo) => void
    deleteTodo: (_id: string) => void
}

const TodoItem: React.FC<Props> = ({todos, updateTodo, deleteTodo}) => {
    const [todo, setTodo] = useState("");
    const [modalValue, setModalValue] = useState<Todo | {}>()

    const [isOpen, setIsOpen] = useState(false)

    function onChange(e) {
        setModalValue({...modalValue, text: e.target.value});
    }

    function onClose() {
        setIsOpen(false)
    }

    function handleEditSubmit(e) {
        e.preventDefault();
        // editTodo(modalValue.id, modalValue)
        setModalValue({
            name: '',
            description: ''
        })
        setIsOpen(false)
    }

    function handleEditClick(todo) {
        setIsOpen(true)
        setModalValue({
            name: todo.name,
            description: todo.description
        })
        console.log(todo)
    }

    return (
        <VStack
            divider={<StackDivider/>}
            borderColor='gray.100'
            borderWidth='2px'
            p='4'
            borderRadius='lg'
            w='100%'
            maxW={{base: '90vw', sm: '80vw', lg: '50vw', xl: '40vw'}}
            alignItems='stretch'
        >
            {todos && todos.length > 0 && todos.map((todo) => (
                <HStack key={todo._id}>
                    <Heading fontSize='xl'>{todo.name}</Heading>
                    <Text mt={4}>{todo.description}</Text>
                    <Spacer/>
                    <Badge m='15' fontSize='0.8em' colorScheme={todo.status === 'incomplete' ? 'red' : 'green'}>
                        {todo.status}
                    </Badge>
                    <Spacer/>
                    <IconButton
                        color="green.500"
                        icon={<FaCheck/>}
                        isRound={true}
                        onClick={() => handleEditClick(todo)}
                        aria-label={"lege"}/>
                    <IconButton
                        color="blue.500"
                        icon={<FaPen/>}
                        isRound={true}
                        onClick={() => updateTodo(todo)}
                        aria-label={"lege"}/>
                    <IconButton
                        color="red.500"
                        icon={<FaTrash/>}
                        isRound={true}
                        onClick={() => deleteTodo(todo._id)}
                        aria-label={"lege"}/>

                    {/* modal for editing a todo */}
                    <Modal isOpen={isOpen} onClose={onClose}>
                        <ModalOverlay/>
                        <ModalContent>
                            <ModalHeader>Edit Your Todo</ModalHeader>
                            <ModalCloseButton/>
                            <form onSubmit={handleEditSubmit}>
                                <ModalBody>
                                    <Input
                                        value={modalValue.name}
                                        variant="outline"
                                        type="text"
                                        placeholder="Update your todo..."
                                        onChange={onChange}/>
                                    <Input
                                        value={modalValue.description}
                                        variant="outline"
                                        type="text"
                                        placeholder="Update your todo..."
                                        onChange={onChange}/>
                                </ModalBody>
                                <ModalFooter>
                                    <Button colorScheme="teal" mr={3} onClick={onClose}>
                                        Close
                                    </Button>
                                    <Button type="submit" colorScheme="teal" mr={3}>
                                        Update
                                    </Button>
                                </ModalFooter>
                            </form>

                        </ModalContent>
                    </Modal>
                </HStack>
            ))}
        </VStack>
    )
}

export default TodoItem
