import React, {useEffect, useState} from 'react'
import TodoItem from '../../components/TodoItem'
import AddTodo from '../../components/AddTodo'
import {getTodos, createTodo, updateTodo, deleteTodo} from '../../api/TodoAPI'
import {Heading} from '@chakra-ui/react'
import {VStack} from '@chakra-ui/react';

const App: React.FC = () => {
    const [todos, setTodos] = useState<Todo[]>([])

    useEffect(() => {
        fetchTodos()
    }, [])

    const fetchTodos = (): void => {
        getTodos()
            .then((todos: Todo[] | any) => setTodos(todos))
            .catch((err: Error) => console.log(err))
    }

    const handleSaveTodo = (e: React.FormEvent, formData: Todo): void => {
        e.preventDefault()
        console.log(formData)
        createTodo(formData)
            .then(({status, data}) => {
                if (status !== 201) {
                    throw new Error('Error! Todo not saved')
                }
                setTodos(data.todos)
            })
            .catch((err) => console.log(err))
    }

    const handleUpdateTodo = (todo: Todo): void => {
        updateTodo(todo._id, todo)
            .then(({status, data}) => {
                if (status !== 200) {
                    throw new Error('Error! Todo not updated')
                }
                setTodos(data.todos)
            })
            .catch((err) => console.log(err))
    }

    const handleDeleteTodo = (_id: string): void => {
        deleteTodo(_id)
            .then(({status, data}) => {
                if (status !== 200) {
                    throw new Error('Error! Todo not deleted')
                }
                setTodos(data.todos)
            })
            .catch((err) => console.log(err))
    }

    return (
        <VStack p={3}>
            <Heading
                mb='8'
                fontWeight='extrabold'
                size='2xl'
                bgClip='text'>
                TODO APP
            </Heading>
            <TodoItem todos={todos} updateTodo={handleUpdateTodo} deleteTodo={handleDeleteTodo}/>
            <AddTodo createTodo={handleSaveTodo}/>
        </VStack>
    )
}

export default App
