import React, {useEffect, useState} from 'react'
import TodoItem from '../../components/TodoItem'
import AddTodo from '../../components/AddTodo'
import {getTodos, createTodo, updateTodo, deleteTodo} from '../../api/TodoAPI'
import {Heading} from '@chakra-ui/react'
import {VStack} from '@chakra-ui/react';
import {useDispatch, useSelector} from "react-redux";
import {getTodosAction} from "../../store/actions";
import {RootState} from "../../store";

const App: React.FC = () => {
    const dispatch = useDispatch();
    const [todosData, setTodosData] = useState<Todo[]>([])

    useEffect(() => {
        fetchTodos()
    }, [])

    const fetchTodos = () => {
        dispatch(getTodosAction());
    };

    const todoListData = useSelector((state: RootState) => state.todoList);
    const {todos} = todoListData;

    const handleSaveTodo = (e: React.FormEvent, formData: Todo): void => {
        e.preventDefault()
        console.log(formData)
        createTodo(formData)
            .then(({status, data}) => {
                if (status !== 201) {
                    throw new Error('Error! Todo not saved')
                }
                setTodosData(data.todos)
            })
            .catch((err) => console.log(err))
    }

    const handleUpdateTodo = (todo: Todo): void => {
        updateTodo(todo._id, todo)
            .then(({status, data}) => {
                if (status !== 200) {
                    throw new Error('Error! Todo not updated')
                }
                setTodos(data.todos)
            })
            .catch((err) => console.log(err))
    }

    const handleDeleteTodo = (_id: string): void => {
        deleteTodo(_id)
            .then(({status, data}) => {
                if (status !== 200) {
                    throw new Error('Error! Todo not deleted')
                }
                setTodos(data.todos)
            })
            .catch((err) => console.log(err))
    }

    return (
        <VStack p={3}>
            <Heading
                mb='8'
                fontWeight='extrabold'
                size='2xl'>
                TODO APP
            </Heading>
            <AddTodo createTodo={handleSaveTodo}/>
            <TodoItem todos={todos} updateTodo={handleUpdateTodo} deleteTodo={handleDeleteTodo}/>
        </VStack>
    )
}

export default App
