import React, {useEffect, useState} from 'react'
import TodoItem from '../../components/TodoItem'
import AddTodo from '../../components/AddTodo'
import {getTodos, createTodo, updateTodo, deleteTodo} from '../../api/TodoAPI'
import {Heading} from '@chakra-ui/react'
import {VStack} from '@chakra-ui/react';
import {useDispatch, useSelector} from "react-redux";
import {createTodoAction, deleteTodoAction, getTodosAction} from "../../store/actions";
import {RootState} from "../../store";

const App: React.FC = () => {
    const dispatch = useDispatch();
    const [todosData, setTodosData] = useState<Todo[]>([])

    useEffect(() => {
        fetchTodos()
    }, [])

    const fetchTodos = () => {
        dispatch(getTodosAction());
    };

    const todoListData = useSelector((state: RootState) => state.todoList);
    const {todos, loading} = todoListData;

    const todoCreateData = useSelector((state: RootState) => state.todoCreate);

    const handleSaveTodo = (e: React.FormEvent, formData: Todo): void => {
        e.preventDefault()
        console.log(formData)
        dispatch(createTodoAction(formData));
        fetchTodos();
    }

    const handleUpdateTodo = (todo: Todo): void => {
        updateTodo(todo._id, todo)
            .then(({status, data}) => {
                if (status !== 200) {
                    throw new Error('Error! Todo not updated')
                }
                setTodosData(data.todos)
            })
            .catch((err) => console.log(err))
    }

    const handleDeleteTodo = (_id: string): void => {
        dispatch(deleteTodoAction(_id));
    }

    return (
        <VStack p={3}>
            <Heading
                mb='8'
                fontWeight='extrabold'
                size='2xl'>
                TODO APP
            </Heading>
            <AddTodo createTodo={handleSaveTodo}/>
            {!loading ? <TodoItem todos={todos} updateTodo={handleUpdateTodo} deleteTodo={handleDeleteTodo}/> : <></>}
        </VStack>
    )
}

export default App
