import React, {useState} from 'react'
import {Box, Button, HStack, Input, useToast} from '@chakra-ui/react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

type Props = {
    createTodo: (e: React.FormEvent, formData: Todo | any) => void
}

const AddTodo: React.FC<Props> = ({createTodo}) => {
    const [content, setContent] = useState({
        name: '',
        description: '',
        startDate: new Date(),
        endDate: new Date()
    });
    const toast = useToast();

    const onChange = e => {
        setContent({...content, [e.target.name]: e.target.value});
    };

    function handleSubmit(e) {
        e.preventDefault();
        if (!content) {
            toast({
                title: 'No content',
                status: 'error',
                duration: 2000,
                isClosable: true,
            });
            return;
        } else {
            createTodo(e, content);
        }
    }

    return (
        <form>
            <Box borderWidth='1px' borderRadius='lg' overflow='hidden'>
                <HStack mt='8'>
                    <Text> Name: </Text>
                    <Input
                        placeholder='name'
                        name='name'
                        onChange={onChange}
                    />
                    Description: <Input
                    placeholder='description'
                    name='description'
                    onChange={onChange}
                />
                    Start Date: <DatePicker selected={content.startDate}
                                            onChange={(date: Date) => setContent({...content, startDate: date})}/>
                    End Date: <DatePicker selected={content.endDate}
                                          onChange={(date: Date) => setContent({...content, endDate: date})}/>
                </HStack>
            </Box>
            <HStack mt='8'>
                <Button onClick={handleSubmit} colorScheme='pink' px='8' type='submit'>
                    Add Todo
                </Button>
            </HStack>
        </form>
    )
}

export default AddTodo
