import React, {useEffect, useState} from 'react'
import TodoItem from '../../components/TodoItem'
import AddTodo from '../../components/AddTodo'
import {getTodos, createTodo, updateTodo, deleteTodo} from '../../api/TodoAPI'
import {Heading} from '@chakra-ui/react'
import {VStack} from '@chakra-ui/react';
import {useDispatch, useSelector} from "react-redux";
import {createTodoAction, deleteTodoAction, getTodosAction, updateTodoAction} from "../../store/actions";
import {RootState} from "../../store";
import Loading from "../../components/Loading";

const App: React.FC = () => {
    const dispatch = useDispatch();
    const [todosData, setTodosData] = useState<Todo[]>([])
    const [change, setChange] = useState(false);

    useEffect(() => {
        fetchTodos()
    }, [])

    const fetchTodos = () => {
        dispatch(getTodosAction());
    };

    const todoListData = useSelector((state: RootState) => state.todoList);
    const {todosList, loadingList} = todoListData;

    const handleSaveTodo = (e: React.FormEvent, formData: Todo): void => {
        e.preventDefault()
        console.log(formData)
        dispatch(createTodoAction(formData));
        fetchTodos();
    }

    const handleUpdateTodo = (todo: Todo): void => {
        dispatch(updateTodoAction(todo));
        fetchTodos();
    }

    const handleDeleteTodo = (_id: string): void => {
        dispatch(deleteTodoAction(_id));
        fetchTodos();
    }

    return (
        <VStack p={3}>
            <Heading
                mb='8'
                fontWeight='extrabold'
                size='2xl'>
                TODO APP
            </Heading>
            <AddTodo createTodo={handleSaveTodo}/>
            {!loadingList ?
                <TodoItem todos={todosList} updateTodo={handleUpdateTodo} deleteTodo={handleDeleteTodo}/> :
                <Loading/>}
        </VStack>
    )
}

export default App
