import React from 'react'
import {
    HStack,
    VStack,
    Text,
    IconButton,
    StackDivider,
    Spacer,
    Badge, Heading,
} from '@chakra-ui/react';
import {FaTrash, FaPen, FaCheck} from "react-icons/fa";

type Props = TodoProps & {
    updateTodo: (todo: Todo) => void
    deleteTodo: (_id: string) => void
}

const Todo: React.FC<Props> = ({todos, updateTodo, deleteTodo}) => {
    return (
        <VStack
            divider={<StackDivider/>}
            borderColor='gray.100'
            borderWidth='2px'
            p='4'
            borderRadius='lg'
            w='100%'
            maxW={{base: '90vw', sm: '80vw', lg: '50vw', xl: '40vw'}}
            alignItems='stretch'
        >
            {todos.map((todo) => (
                <HStack key={todo._id}>
                    <Heading fontSize='xl'>{todo.name}</Heading>
                    <Text mt={4}>{todo.description}</Text>
                    <Badge m='15' fontSize='0.8em' colorScheme={todo.status === 'incomplete' ? 'red' : 'green'}>
                        {todo.status}
                    </Badge>
                    <Spacer/>
                    <IconButton
                        color="green.500"
                        icon={<FaCheck/>}
                        isRound={true}
                        onClick={() => updateTodo(todo)}
                        aria-label={"lege"}/>
                    <IconButton
                        color="blue.500"
                        icon={<FaPen/>}
                        isRound={true}
                        onClick={() => updateTodo(todo)}
                        aria-label={"lege"}/>
                    <IconButton
                        color="red.500"
                        icon={<FaTrash/>}
                        isRound={true}
                        onClick={() => deleteTodo(todo._id)}
                        aria-label={"lege"}/>
                </HStack>
            ))}
        </VStack>
    )
}

export default Todo
